function wp_lib_post_render($){var metaTypeSelector=$('#meta-media-type-selector');var currentMediaType=metaTypeSelector.val();$('.meta-media-type-section').each(function(e,obj){var theSection=$(obj);if(theSection.val()!=currentMediaType){theSection.hide();}});metaTypeSelector.change(function(){currentMediaType=wp_lib_update_meta_box_display(metaTypeSelector.val(),currentMediaType);});var isbnField=$('[name="wp_lib_item_isbn"]');if(isbnField.val()!==''){var isbnVal=hyphenateISBN(isbnField.val());if(typeof isbnVal==='string'){isbnField.val(isbnVal);}}}
function wp_lib_update_meta_box_display(newMediaType,oldMediaType){if(oldMediaType===newMediaType){return newMediaType;}
jQuery('.meta-media-type-section').each(function(i,obj){var theSection=jQuery(obj);if(theSection.val()===oldMediaType){theSection.hide(400);}
else if(theSection.val()===newMediaType){theSection.show(400);}});return newMediaType;}
jQuery(function($){$('div#submitpost').on('click','[type="submit"]',function(e){var currentMediaType=$('#meta-media-type-selector').val();$('.meta-media-type-section').each(function(i,obj){var theSection=jQuery(obj);if(theSection.val()===currentMediaType){return true;}else{$(theSection.find('td.meta-input-wrapper *')).each(function(i,metaField){if(metaField.tagName!='OPTION'){$(metaField).val('');}});}});});});
